# -*- coding: utf-8 -*-
from __future__ import annotations

from setuptools import find_packages
from setuptools import setup


setup(
    name="pioreactor-logs2discord",
    version="0.3.0",
    license="MIT",
    description="Push logs generated by Pioreactors to your Discord workspace",
    long_description=open("README.md").read(),
    long_description_content_type="text/markdown",
    author_email="hello@pioreactor.com",
    author="Pioreactor",
    url="https://github.com/Pioreactor/pioreactor-logs2discord",
    packages=find_packages(),
    include_package_data=True,
    entry_points={"pioreactor.plugins": "pioreactor_logs2discord = pioreactor_logs2discord"},
)
