def 列表_全员加法计算(列表, 要加的数值):
    """
    将列表中的每个元素都加上指定的数值，并返回结果列表。

    参数:
        - 列表 (list): 包含数字的列表
        - 要加的数值 (int, float): 要添加到每个元素的数值

    返回:
        - 新列表 (list): 包含所有元素加上指定数值后的结果

    如果遇到任何异常（例如类型错误），则返回错误信息。
    """
    try:
        新列表 = []

        for 元素 in 列表:
            try:
                # 尝试将元素加上指定的数值
                新列表.append(元素 + 要加的数值)
            except Exception as e:
                # 如果出现异常，返回错误信息
                return f"处理失败: {e}"

        return 新列表
    except Exception as e:
        # 捕获所有异常并返回错误信息
        return f"处理失败: {e}"